import React from "react";
// Reference: https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/match

export default function Home(props) {
    const [balance, setBalance] = React.useState(props.balance)
    const token = props.token
    const [postObj, setPostObj] = React.useState({
        id: props.id,
        amount: ""
    })


    function handleChange(event) {
        setPostObj(prevData => ({
            ...prevData,
            amount:event.target.value
        }))
    }

    function check() {
        const regex = /(0|[1-9][0-9]*)/
        let result
        const amount  = postObj.amount.split('.')
        if(amount.length === 1){
            result = postObj.amount.match(regex)[0] === amount[0]
        }
        else if (amount.length === 2){
            result = postObj.amount.match(regex)[0] === amount[0] && (amount[1] <= 99 && amount[0] >= 0) && (amount[0] >=0 && amount[0]<= 4294967295)
        }
        console.log(result)
        // const found = postObj.amount.match(regex);
        // console.log(postObj.amount[0])
        // console.log(found[0] === postObj.amount.split('.')[0])
        // const regex = /[0-9]{2}/
        // const found = postObj.amount.match(regex);
        // console.log(found)
        // console.log(found[0] === postObj.amount)
    }

    function deposit() {
        fetch('http://localhost:8081/bankSys/user/deposit', {
            method: 'POST',
            headers: {
                'token': token,
                'Accept' : 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(postObj),
        })
            .then(response => response.json())
            .then(data => {
                setBalance(data.data)
                console.log('Success:', data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    function withdrawal() {
        fetch('http://localhost:8081/bankSys/user/withdrawal', {
            method: 'POST',
            headers: {
                'token': token,
                'Accept' : 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(postObj),
        })
            .then(response => response.json())
            .then(data => {
                setBalance(data.data)
                console.log('Success:', data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    return (
        <div>
            <h3>Welcome to Home Page</h3>
            <p>{`Current balance in your account: $${balance}`}</p>
            <input
                placeholder={0}
                onChange={handleChange}
                value={postObj.amount}
            />
            {/*<p>{props.username}</p>*/}
            {/*<p>{props.id}</p>*/}
            {/*<p>{props.token}</p>*/}

            <button onClick={check}>Check</button>
            <button onClick={deposit}>Deposit</button>
            <button onClick={withdrawal}>Withdraw</button>
        </div>
    )
}
