import React from "react";
// zishan01
// 12345

export default function Register() {
    const [status, setStatus] = React.useState("")
    const [formData, setFormData] = React.useState(
        {
            username: "",
            password: ""
        }
    )
    const [waringText, setWarningText] = React.useState({
        usernameReminder: "",
        passwordReminder: ""
    })

    function checkInput(data) {
        const regex = /[_\\-\\.0-9a-z]/
        let result = true
        for (let char of data) {
            if (char.match(regex) === null) {
                result = false
            }
        }
        return result && (data.length <= 127 && data.length >= 1)
    }


    function handleChange(event) {
        event.preventDefault()
        console.log(event.target.value)
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [event.target.name]: event.target.value
            }
        })
    }

    function handleSubmit(event) {
        event.preventDefault()

        const nameIsValid = checkInput(formData.username)
        const passwordIsValid = checkInput(formData.password)

        setWarningText(prevState => ({
            usernameReminder: "",
            passwordReminder: ""
        }))

        if (nameIsValid && passwordIsValid) {

            fetch('http://localhost:8081/bankSys/user/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            })
                .then(response => response.json())
                .then(data => {
                    // setBalance(data.data.user.balance)
                    if (data.status === 200) {
                        setStatus(`congratulations ${formData.username}, You have registered successfully!`)
                    } else {
                        setStatus(`This username is used, please try another one~`)
                    }
                })
                .catch((error) => {
                    setStatus(`Error: ${error}`)
                });
        }
        if (!nameIsValid || !passwordIsValid) {
            setWarningText({
                usernameReminder: nameIsValid ? "" : "Invalid username!",
                passwordReminder: passwordIsValid ? "" : "Invalid password!"
            })
        }
    }

    return (
        <div className="register">
            <h3>Welcome to register page</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="username"
                    name="username"
                    onChange={handleChange}
                    value={formData.username}
                />
                {waringText.usernameReminder}
                <input
                    type="text"
                    placeholder="password"
                    name="password"
                    onChange={handleChange}
                    value={formData.password}
                />
                {waringText.passwordReminder}
                <button>Register</button>
            </form>
            <p>{status}</p>
        </div>
    )
}
