import React, {useEffect} from "react";
import {nanoid} from "nanoid"

export default function FetchData() {
    const [count, setCount] = React.useState(0)
    const [myData, setMyData] = React.useState([{
        question: "",
        answers: [],
        correctAnswer: "",
        selectedAnswer: "",
        id: ""
    }])

    useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=6", {method:'POST'})
            .then(res => res.json())
            .then(data => {
                setMyData(data.results.map(item => ({
                    question: item.question.replace(/&quot;/g, '"').replace(/&#039;/g, '"'),
                    answers: [item.correct_answer, ...item.incorrect_answers]
                        .sort(() => Math.random() - 0.5),
                    correctAnswer: item.correct_answer.replace(/&quot;/g, '"').replace(/&#039;/g, '"'),
                    selectedAnswer: "",
                    id: nanoid()
                })))
            })
    }, [count])

    return (
        <h2>{myData[0].question}</h2>
    )
}
