import React from "react";
// zishan01
// 12345

export default function Register() {
    const [status, setStatus] = React.useState("")
    const [formData, setFormData] = React.useState(
        {
            username: "",
            password: ""
        }
    )

    function checkUsername(){
        const regex = /[_\\-\\.0-9a-z]/
        let result = true
        for (let char of formData.username){
            alert(char.match(regex)[0])
            // if(char.match(regex)[0] != char){
            //     result = false
            // }
        }
        console.log(result)
    }

    function checkPassword() {

    }

    function handleChange(event) {
        event.preventDefault()
        console.log(event.target.value)
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [event.target.name]: event.target.value
            }
        })
    }

    function handleSubmit(event){
        event.preventDefault()
        fetch('http://localhost:8081/bankSys/user/create', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then(response => response.json())
            .then(data => {
                // setBalance(data.data.user.balance)
                if(data.status === 200){
                    setStatus(`Success: msg: ${data.msg}`)
                }
                else{
                    setStatus(`This username is used, please try another one!`)
                }
            })
            .catch((error) => {
                setStatus(`Error: ${error}`)
            });
    }


    return (
        <div className="register">
            <h3>Welcome to register page</h3>
            <form onSubmit={checkUsername}>
                <input
                    type="text"
                    placeholder="username"
                    name="username"
                    onChange={handleChange}
                    value={formData.username}
                />
                <input
                    type="text"
                    placeholder="password"
                    name="password"
                    onChange={handleChange}
                    value={formData.password}
                />
                <button>Register</button>
            </form>
            <p>{status}</p>
        </div>
    )
}
