import React from "react";
// Reference: https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/match

export default function Home(props) {
    const [balance, setBalance] = React.useState(props.balance)
    const [reminderText, setReminderText] = React.useState("")
    const token = props.token
    // postObj saves the userId and the amount of money entered in the input box
    const [postObj, setPostObj] = React.useState({
        id: props.id,
        amount: ""
    })

    /**
     * updateAmount() function is used to update the amount every time the user make changes
     * @param event
     */
    function updateAmount(event) {
        setPostObj(prevData => ({
            ...prevData,
            amount: event.target.value
        }))
    }

    // function checkNumericInput() {
    //     const regex = /(0|[1-9][0-9]*)/
    //     let result
    //     const amount = postObj.amount.split('.')
    //     if (amount.length === 1) {
    //         result = postObj.amount.match(regex)[0] === amount[0]
    //     } else if (amount.length === 2) {
    //         result = postObj.amount.match(
    //                 regex)[0] === amount[0] &&
    //             (amount[1] <= 99 && amount[0] >= 0) &&
    //             (amount[0] >= 0 && amount[0] <= 4294967295)
    //     }
    //     return result
    // }

    /**
     * Deposit money
     */
    function deposit() {
        if(postObj.amount <= 4294967295.99){
            fetch('http://localhost:8081/bankSys/user/deposit', {
                method: 'POST',
                headers: {
                    'token': token,
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(postObj),
            })
                .then(response => response.json())
                .then(data => {
                    if(data.status === 500){
                        setReminderText("Invalid input")
                    }else{
                        setReminderText(`Successfully deposited $${postObj.amount}!`)
                        setBalance(data.data)
                    }
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
        }
        else{
            setReminderText("Invalid input")
        }
    }

    /**
     * Withdrawal money
     */
    function withdrawal() {
            fetch('http://localhost:8081/bankSys/user/withdrawal', {
                method: 'POST',
                headers: {
                    'token': token,
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(postObj),
            })
                .then(response => response.json())
                .then(data => {
                    if(data.status === 500){
                        setReminderText(data.msg)
                    }else{
                        setReminderText(`Successfully withdraw $${postObj.amount}!`)
                        setBalance(data.data)
                    }
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
    }

    return (
        <div className="home">
            <div className="home-header">
                <h1 className="home-title">`Hi, ${props.username} ðŸ‘‹`</h1>
                <button className="btn home-logout-btn" onClick={props.changeLoginStatus}>Logout</button>
            </div>

            <p className="hone-text"> Current balance <span className="home-balance"> ${balance}</span></p>
            <input
                placeholder={0}
                onChange={updateAmount}
                name="amount"
                value={postObj.amount}
                className="home-form-input"
            />
            <p className="home-reminderText">{reminderText}</p>
            <button className="btn home-deposit-btn" onClick={deposit}>Deposit</button>
            <button className="btn home-withdrawal-btn" onClick={withdrawal}>Withdraw</button>
        </div>
    )
}
