import React from "react";

export default function Login(props) {
    const [loginFormData, setLoginFormData] = React.useState(
        {
            username: "",
            password: ""
        }
    )
    const [loginStatusText, setLoginStatusText] = React.useState("")

    function updateUserLoginData(event) {
        event.preventDefault()
        console.log(event.target.value)
        setLoginFormData(prevFormData => {
            return {
                ...prevFormData,
                [event.target.name]: event.target.value
            }
        })
    }

    function handleSubmit(event) {
        event.preventDefault()
        fetch('http://localhost:8081/bankSys/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(loginFormData),
        })
            .then(response => response.json())
            .then(data => {
                if (data.status === 200) {
                    setLoginStatusText("Login Successfully!")
                    props.changeLoginStatus(data)
                } else if (data.status === 500) {
                    setLoginStatusText("Username or password is wrong")
                }
            })
            .catch((error) => {
                setLoginStatusText(`Error: ${error}`)
            });
    }

    return (
        <div className="login">
            <h3>welcome to login page</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="username"
                    onChange={updateUserLoginData}
                    value={loginFormData.username}
                />
                <input
                    type="text"
                    placeholder="password"
                    onChange={updateUserLoginData}
                    value={loginFormData.password}
                />
                <button>Login</button>
            </form>
            <p>{loginStatusText}</p>
        </div>
    )
}
