import React from "react";
import './App.css';
import FetchData from "./components/FetchData";
import Login from "./components/Login";
import Register from "./components/Register";
import Home from "./components/Home";
import {Routes, Route, Link} from "react-router-dom"


function App() {
    const [isLoginStatus, SetIsLoginStatus] = React.useState(true)
    const [userInfo, setUserInfo] = React.useState(
        {
            username: "",
            id:"",
            balance:"",
            token:""
        }
    )

    function handleChange(userInfo) {
        console.log(userInfo)

        setUserInfo({
            username: userInfo.data.user.username,
            id: userInfo.data.user.id,
            balance:userInfo.data.user.balance,
            token:userInfo.data.token
        })
        SetIsLoginStatus(prevStatus => !prevStatus)

        console.log("----")
        console.log(userInfo)
    }

    return (
        <div>
            <nav>
                <Link to="/">Home</Link>
                <Link to="/about">About</Link>
                <Link to="/contact">Contact</Link>
            </nav>

            <Routes>
                <Route exact path="/">
                    <Login changeLoginStatus={handleChange}/>
                </Route>
                {/*<Route path="/about">*/}
                {/*    <About/>*/}
                {/*</Route>*/}
                {/*<Route path="/contact">*/}
                {/*    <Contact/>*/}
                {/*</Route>*/}
            </Routes>
            {isLoginStatus && <Login changeLoginStatus={handleChange}/>}
            {/*<Register/>*/}
            {/*<FetchData/>*/}
            {!isLoginStatus && <Home {...userInfo}/>}
        </div>
    )
}

export default App;
