import React from "react";
import './App.css';
import Login from "./components/Login";
import Register from "./components/Register";
import Home from "./components/Home";
import {Routes, Route, Link} from "react-router-dom"

function App() {
    const [isLoginStatus, SetIsLoginStatus] = React.useState(true)
    const [userInfo, setUserInfo] = React.useState(
        {
            username: "",
            id: "",
            balance: "",
            token: ""
        }
    )

    function handleChange(userInfo) {
        console.log(userInfo)

        setUserInfo({
            username: userInfo.data.user.username,
            id: userInfo.data.user.id,
            balance: userInfo.data.user.balance,
            token: userInfo.data.token
        })
        SwitchLoginStatus()
    }

    function SwitchLoginStatus() {
        SetIsLoginStatus(prevStatus => !prevStatus)
    }

    return (
        <div>
            <nav>
                <Link to="/">Register Page</Link>
                <Link to="/about">Home Page</Link>
            </nav>

            <Routes>
                <Route exact path="/" element={<Register/>}/>
                <Route exact path="/about"
                       element={
                           isLoginStatus ?
                               <Login changeLoginStatus={handleChange}/>
                               : <Home
                                   username={userInfo.username}
                                   id={userInfo.id}
                                   balance={userInfo.balance}
                                   token={userInfo.token}
                                   changeLoginStatus={SwitchLoginStatus}
                               />}
                />
            </Routes>
        </div>
    )
}

export default App;
