import React, {useEffect} from "react";

// post: https://stackoverflow.com/questions/38510640/how-to-make-a-rest-post-call-from-reactjs-code
// post: https://reactnative.dev/docs/network

export default function FetchData() {
    const [count, setCount] = React.useState(0)
    const [balance, setBalance] = React.useState(0)
    // const [myData, setMyData] = React.useState([{
    //     question: "",
    //     answers: [],
    //     correctAnswer: "",
    //     selectedAnswer: "",
    //     id: ""
    // }])

    function changeCount() {
        setCount(prev => prev + 1)
    }

    /**
     * fetch info from api (GET)
     */
    useEffect(() => {
        fetch("http://localhost:8081/bankSys/login", {method:'POST'})
            .then(res => res.json())
            .then(data => console.log(data))
    }, [count])

    /**
     * POST : http://localhost:8081/bankSys/login
     *
     * https://mywebsite.com/endpoint/
     */
    function handleClick() {
        const data = {
            username:"123456",
            password:"123456"
        };
        fetch('http://localhost:8081/bankSys/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        })
            .then(response => response.json())
            .then(data => {
                setBalance(data.data.user.balance)
                // console.log('Success:', data.data.user.balance);
            })
            .catch((error) => {
                console.error('Error:', error);
            });


        // fetch('http://localhost:8081/bankSys/login', {
        //     method: 'POST',
        //     headers: {
        //         Accept: 'application/json',
        //         'Content-Type': 'application/json'
        //     },
        //     body: JSON.stringify({
        //         firstParam: 'yourValue',
        //         secondParam: 'yourOtherValue'
        //     })
        // });
    }

    return (
        <div>
            <p>{balance}</p>
            <button onClick={handleClick}>POST INFO</button>
            <button onClick={changeCount}>GET INFO</button>
        </div>
    )
}
